    SET `Name`=eName, `Desc` = `description`,Cost=cost,
    EquipmentCondition=equipmentCondition,
    Brand=brand,
    Section=section,`Type`=`type`,
    `Status`=`status`, DateReceived=dateReceived,Supplier=supplier, `Serial`=barcode
    where EquipmentID = id;
    end if;
    
    IF( row_count() > 0) THEN
		SELECT TRUE;
        END IF;
    
    COMMIT WORK;
END ;;
