	BEGIN
	GET CURRENT DIAGNOSTICS CONDITION 1 errno = MYSQL_ERRNO;
	SELECT errno AS MYSQL_ERROR;
	ROLLBACK;
	END;
		
	START TRANSACTION;
    BEGIN
    select @equipID := EquipmentID, @cost := Cost FROM equipment as e where e.`Serial`=`serial`; 
    
    insert into equipmenthistory(`Date`,`Condition`,`Picture`,`Value`,`equipmentID`,`userID`,Active,`Desc`)
    values(now(),cond,null,@cost,@equipID,userID,True,concat("Installed at ",coords));
    
    update equipment
    set LocationGps = coords, `Status`=3, UserID=null
    Where EquipmentID = @equipID;
    Commit work;
    IF( row_count() > 0) THEN
		SELECT TRUE;
	END IF;
    end;
END ;;
